    public function purchaseCallback()
    {
        $this->request->validate([
            'transaction_id' => 'required|string',
        ]);

        $const = config('payment.gateway.kkiapay.environment') == 'sandbox' ? 'https://api-sandbox.kkiapay.me' : 'https://api.kkiapay.me';

        $response = Http::withHeaders([
            'Accept' => 'application/json',
            'X-API-KEY' => config('payment.gateway.kkiapay.public_key'),
            'X-PRIVATE-KEY' => config('payment.gateway.kkiapay.private_key'),
            'X-SECRET-KEY' => config('payment.gateway.kkiapay.secret_key')
        ])->post($const . '/api/v1/transactions/status', [
            "transactionId" => $this->request->input('transaction_id'),
        ]);

        if ($response->successful() && $response->object()->transactionId == $this->request->input('transaction_id')) {
            Cart::session(auth()->user()->id);

            foreach (Cart::getContent() as $item) {
                $order = new Order();
                $order->user_id = auth()->user()->id;
                $order->orderable_id = $item->attributes->orderable_id;
                $order->orderable_type = $item->attributes->orderable_type;
                $order->payment = 'kkiapay';
                $order->amount = $item->price;
                $order->currency = config('settings.currency', 'USD');
                $order->payment_status = 1;
                $order->transaction_id = $response->object()->transactionId;
                $order->save();
            }

            Cart::clear();

            echo '<script type="text/javascript">
                var opener = window.opener;
                if(opener) {
                    opener.Payment.purchaseSuccess();
                    window.close();
                }
                
             </script>';
            exit();

        } else {
            abort(403, 'Unable to authenticate KKiaPay');
        }
    }
